import { createSlice,createAsyncThunk } from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    status: '',
    user: ''
}

export const userLoggedIn = createAsyncThunk('user/loggedin', async (details)=> {
    const user = axios.post('/user/login', details).then(res => {

        sessionStorage.setItem('user-e-commerce',JSON.stringify(res.data));
        window.location.href = '/user/profile'

        return res.data;

      }).catch(err => {
        console.log(err)
      })

      return user
})

const userSlice = createSlice({
    name: 'user',
    initialState,
    reducer: {

    },
    extraReducers: {
        [userLoggedIn.pending]: (state,action) => {
            state.status = 'Loading'
        },
        [userLoggedIn.fulfilled]: (state,action) => {
            state.status = 'Success';
            state.user = state.user.concat(action.payload)
        },
        [userLoggedIn.rejected]: (state,action) => {
            state.status = 'rejected'
        }
    }
})

export default userSlice.reducer
