import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { Button, Input, TextField, Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    maxWidth: '50vw',
    margin: '0 auto'
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    width: '100%',
    padding: theme.spacing(5)
  },
  form:{
    display: 'flex',
    flexDirection: "column"
  },
  heading: {
      textAlign: 'center'
  }
}))

function ModalForm({ handleClick,handleClose,open }) {
  const classes = useStyles();
  const [formData, setFormData] = useState({
      name: '',
      price: '',
      category: '',
      description: '',
      image: '',
      stock: ''
  })
  const closeModal = () => {
    handleClose();
  };
  const changeHandler = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value })
  }
  const submitHandler = (e) => {
      e.preventDefault();

    console.log(formData)
  }

  return (
    <div>
      <Modal
        className={classes.modal}
        open={open}
        onClose={closeModal}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <Typography variant = 'h5' className={classes.heading} gutterBottom={true}>Add Products</Typography>
            <form className={classes.form} noValidate autoComplete="off" onSubmit={submitHandler}>
                <Input defaultValue={formData.name} name='name' placeholder='name of the product' onChange={changeHandler}  required/>
                <Input placeholder="price" name='price' onChange={changeHandler}/>
                <Input placeholder="stock" name='stock' onChange={changeHandler}/>
                <TextField required multiline rows={5} placeholder='description' name='description' onChange={changeHandler}/>
                <Button type='submit'>Submit</Button>
            </form>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}

export default ModalForm
