import React from 'react'
import Slider from "react-slick";

import tshirt from '../image/tshirt.jpg'
import hoodieBack from '../image/hoodieBack.jpg'

function Carousel() {

    let settings = {
        dots: true,
        infinite: true,
        autoPlaySpeed: 3000,
        autoPlay: true,
        slidesToShow: 1,
        slidesToScroll: 1
      };

    return (
        <Slider {...settings} style={{height: '80vh'}}>
            <div>
                <img src={tshirt} alt='background' style={{width: '100%',height: '100%'}}/>
            </div>
            <div style={{height: '80vh',backgroundColor: "grey",marginTop: '15vh', background: `url(${hoodieBack}) 60% -30% / cover fixed`}}>
                <h3>1</h3>
            </div>
        </Slider>
    )
}

export default Carousel
