import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Modal from '@material-ui/core/Modal';
import Backdrop from '@material-ui/core/Backdrop';
import Fade from '@material-ui/core/Fade';
import { Button, FormControl, Input, InputLabel, MenuItem, Select, TextField, Typography } from '@material-ui/core';

const useStyles = makeStyles((theme) => ({
  modal: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    maxWidth: '50vw',
    margin: '0 auto'
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    padding: theme.spacing(2, 4, 3),
    width: '100%',
    padding: theme.spacing(5)
  },
  form:{
    display: 'flex',
    flexDirection: "column",
    '& .MuiInput-root': {
        height: theme.spacing(8)
    }
  },
  heading: {
      textAlign: 'center'
  },
  button: {
    width: theme.spacing(10),
    marginTop: theme.spacing(4),
    margin: '0 auto'
  }
}))

function ModalForm({ handleClick,handleClose,open }) {
  const classes = useStyles();
  const [formData, setFormData] = useState({
      name: '',
      price: '',
      category: '',
      description: '',
      image: '',
      stock: ''
  })
  const closeModal = () => {
    handleClose();
  };
  const changeHandler = (e) => {
      setFormData({ ...formData, [e.target.name]: e.target.value })
  }
  const submitHandler = (e) => {
      e.preventDefault();

    console.log(formData)
  }

  return (
    <div>
      <Modal
        className={classes.modal}
        open={open}
        onClose={closeModal}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <Typography variant = 'h5' className={classes.heading} gutterBottom={true}>Add Products</Typography>
            <form className={classes.form} noValidate autoComplete="off" onSubmit={submitHandler}>
                <Input defaultValue={formData.name} name='name' placeholder='name of the product' onChange={changeHandler}  required/>
                <Input placeholder="price" name='price' onChange={changeHandler}/>
                <Input placeholder="stock" name='stock' onChange={changeHandler}/>
                <TextField required multiline  placeholder='description' name='description' onChange={changeHandler}/>
                <Button type='submit' variant='contained' color='primary' className={classes.button}>Submit</Button>
                <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Age</InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={formData.category}
                  onChange={changeHandler}
                  name='category'
                >
                  <MenuItem value={10}>Ten</MenuItem>
                  <MenuItem value={20}>Twenty</MenuItem>
                  <MenuItem value={30}>Thirty</MenuItem>
                </Select>
              </FormControl>
            </form>
          </div>
        </Fade>
      </Modal>
    </div>
  );
}

export default ModalForm
