import { Button, makeStyles, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from '@material-ui/core'
import { DeleteForever } from '@material-ui/icons';
import React from 'react'
import { useSelector } from 'react-redux';
import demo from '../image/demo.gif'

const useStyles = makeStyles({
    table: {
      width: '90%',
      marginTop: '15vh',
      margin: '0 auto'
    },
    thumbnail: {
        height: '100px',
        widht: '100px'
    },
    tableProduct: {
        display: 'flex',
        '& h6': {
            marginLeft: '10px'
        }
    },
    spanButton: {
      padding: '8px 15px',
      backgroundColor: '#f4f5fd'
    }
  });

function TableComponents() {
    const classes = useStyles()
    const cartItems = useSelector(state => state.cart?.items)
    const handleClick = (action) => {
      console.log(action)
    }

    if(cartItems){
      return (
        <TableContainer className={classes.table}>
            <Typography variant='h4' gutterBottom={true}>Your cart</Typography>
          <Table  component={Paper} aria-label="spanning table">
            <TableHead>
              <TableRow>
                <TableCell >Product</TableCell>
                <TableCell align="right">Price</TableCell>
                <TableCell align="right">Qty.</TableCell>
                <TableCell align="right">Total</TableCell>
                <TableCell align="right">Action</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
                {
                  cartItems.map(single => {
                    return <TableRow >
                      <TableCell className={classes.tableProduct}>
                          <img src={single.image} alt='thumbnail' className={classes.thumbnail}/>
                          <Typography variant='h6'>{single.name}</Typography>
                      </TableCell>
                      <TableCell align="right">{single.price}</TableCell>
                      <TableCell align="right">
                        <span className={classes.spanButton} onClick={() => handleClick('inc')}>+</span>
                        <span className='amount'>{2}</span>
                        <span className='minus btn' onClick={() => handleClick('dec')}>-</span>
                      </TableCell>
                      <TableCell align="right">2345</TableCell>
                      <TableCell align="right">
                          <Button color='secondary'>
                              <DeleteForever />
                          </Button>
                      </TableCell>
                    </TableRow>
                  })
                }
            </TableBody>
          </Table>
        </TableContainer>
        )
    }
}

export default TableComponents
