import { Input, makeStyles, TextField, Typography } from '@material-ui/core'
import React, { useState } from 'react'

const useStyles = makeStyles( theme => ({
    root: {
        margin: theme.spacing.apply(2),
        width: '100%'
    },
    editdetails: {
        width: '100%',
        marginTop: theme.spacing(2),
        '& .MuiTextField-root': {
            width: '100%'
        }
    }
}))


function EditProfile({ user }) {
    const classes = useStyles()

    const [name, setName] = useState(user.name)
    const changeName = (e) => {
        setName(e.target.value)
    }

    return (
        <div className={classes.root}>
            <Typography variant='h5' gutterBottom={true}>Edit your details</Typography>
            <form className={classes.editdetails}>
                <TextField variant = 'outlined' label='Name' defaultValue={name} name='name' type='text' placeholder='Enter new name' onChange = {changeName}/>
            </form>
        </div>
    )
}

export default EditProfile
