import React from 'react'
import { AppBar, Badge, Box, IconButton, InputBase, Menu, MenuItem, Toolbar, Typography } from '@material-ui/core';
// import { SearchIcon,NotificationsIcon,AccountCircle } from '@material-ui/icons'
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import SearchIcon from '@material-ui/icons/Search';
import AccountCircle from '@material-ui/icons/AccountCircle';
import MenuIcon from '@material-ui/icons/Menu';
import { fade, makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom';
import { useState } from 'react';
const useStyles = makeStyles((theme) => ({
    grow: {
      flexGrow: 1,
      '& .MuiBox-root': {
        position:'fixed',
        zIndex: '2000',
        height: '100vh',
        width: '300px',
        top: '15vh',
        backgroundColor: '#f4f5fd',
        right: '0',
        padding: '20px',
        transition: '3s'
      },
      '& .MuiAppBar-root': {
        height: '15vh',
        justifyContent: 'center'
      }
    },
    menuButton: {
      marginRight: theme.spacing(2),
    },
    title: {
      display: 'none',
      [theme.breakpoints.up('sm')]: {
        display: 'block',
      },
      sideNav: {
        position:'absolute',
        height: '100vh',
        width: '300px',
        backgroundColor: '#f4f5fd',
        right: '0'
      }
    },
    search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
        backgroundColor: fade(theme.palette.common.white, 0.25),
      },
      marginRight: theme.spacing(2),
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
        marginLeft: theme.spacing(3),
        width: 'auto',
      },
    },
    searchIcon: {
      padding: theme.spacing(0, 2),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    inputRoot: {
      color: 'inherit',
    },
    inputInput: {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('md')]: {
        width: '20ch',
      },
    },
    sectionDesktop: {
      display: 'none',
      [theme.breakpoints.up('md')]: {
        display: 'flex',
      },
    },
    sectionMobile: {
      display: 'flex',
      [theme.breakpoints.up('md')]: {
        display: 'none',
      },
    },
    anchor: {
      color: 'white',
      textDecoration: 'none'
    },
    login: {
      fontSize: '1.2rem',
    }
  }));
function Navigation() {
    const classes = useStyles()
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(false);

  const handleMobileMenuOpen = (e) => {
    setMobileMoreAnchorEl(!mobileMoreAnchorEl)
  }
  const renderItems = <Box className={classes.sideNav}>
    <Typography variant="h6" color="inherit">
      Login
    </Typography>
    <Typography variant="h6" color="inherit">
      Signup
    </Typography>
    <Typography variant="h6" color="inherit">
      Cart
    </Typography>
  </Box>
    return (
        <div className={classes.grow}>
          <AppBar position="fixed" color='primary'>
          <Toolbar>
            <IconButton
              edge="start"
              className={classes.menuButton}
              color="inherit"
              aria-label="open drawer"
            >
              {/* <MenuIcon /> */}
            </IconButton>
            <Link to='/' className={classes.anchor}>
              <Typography className={classes.title} variant="h6" noWrap>
                Buy It
              </Typography>
            </Link>
            <div className={classes.grow} />
            <div className={classes.search}>
              <div className={classes.searchIcon}>
                <SearchIcon />
              </div>
              <InputBase
              type='search'
                placeholder="Searchâ€¦"
                classes={{
                  root: classes.inputRoot,
                  input: classes.inputInput,
                }}
                inputProps={{ 'aria-label': 'search' }}
              />
            </div>
            <div className={classes.grow} />
            <div className={classes.sectionDesktop}>
              <Link to='/cart' className={classes.anchor}>
                <IconButton aria-label="show cart items" color="inherit">
                  <Badge badgeContent={4} color="secondary">
                    <ShoppingCartIcon/>
                  </Badge>
                </IconButton>
              </Link>
              <Link to='/user/signup' className={classes.anchor}>
                <IconButton color="inherit" className={classes.login}>
                    Sign Up
                </IconButton>
              </Link>
              <Link to='/user/signin' className={classes.anchor}>
                <IconButton color="inherit" className={classes.login}>
                  Login
                </IconButton>
              </Link>
              <Link to='/user/profile' className={classes.anchor}>
                <IconButton
                  edge="end"
                  aria-label="account of current user"
                //   aria-controls={menuId}
                  aria-haspopup="true"
                //   onClick={handleProfileMenuOpen}
                  color="inherit"
                >
                  <AccountCircle />
                </IconButton>
              </Link>
            </div>
            <div className={classes.sectionMobile}>
              <IconButton
                className={classes.login}
                aria-label="show more"
              //   aria-controls={mobileMenuId}
                aria-haspopup="true"
                onClick={handleMobileMenuOpen}
                color="inherit"
              >
                <MenuIcon />
              </IconButton>
            </div>
          </Toolbar>
        </AppBar>
        { mobileMoreAnchorEl ? renderItems : ''}
        </div>
    )
}

export default Navigation
