import { Button, Input, makeStyles, Typography } from '@material-ui/core'
import React from 'react'

const useStyles = makeStyles( theme => ({
    form:{
        display: 'flex',
        width: '90%',
        margin: '0 auto',
        flexDirection: "column",
        '& .MuiInput-root': {
            height: theme.spacing(8)
        }
      },
      shippingDetails: {
          marginTop: theme.spacing(3)
      },
      buttons: {
          display: 'flex',
          justifyContent: 'space-around',
          marginTop: theme.spacing(2)
      }
}))

function CheckoutForm() {
    const classes = useStyles()
    const changeHandler = () => {
        
    }
    const submitHandler = () => {

    }

    return (
        <form className={classes.form} autoComplete="off" onSubmit={submitHandler}>
                <Input  name='name' placeholder='Your Name' onChange={changeHandler}  required={true}/>
                <Input placeholder="email" name='email' onChange={changeHandler}/>
                <Typography variant='h5' gutterBottom={true} className={classes.shippingDetails}>Shipping Details</Typography>
                <Input  name='state' placeholder='State' onChange={changeHandler}  required/>
                <Input placeholder="city" name='City' onChange={changeHandler}/>
                <Input placeholder="street" name='Street' onChange={changeHandler}/>
                <Input placeholder="houseNo" name='houseNo' onChange={changeHandler}/>
                
               <div className={classes.buttons}>
                <Button type='Button' variant='contained' color='primary' >Pay With Khalti</Button>
                <Button type='submit' variant='contained' color='primary' >Cash On delivery</Button>
               </div>
            </form>
    )
}

export default CheckoutForm
